import { useState } from "react";

export default function InkCurveCalculator() {
  const [deckSize, setDeckSize] = useState(60);
  const [nonInkables, setNonInkables] = useState(20);
  const [manaCurve, setManaCurve] = useState(4.5);
  const [result, setResult] = useState(null);

  function calculateMinimumInkables() {
    const turns = Math.ceil(manaCurve);
    const openingHand = 7;
    const drawsByTurn = turns - 1;
    const totalCardsSeen = openingHand + drawsByTurn;

    let inkablesNeeded = turns;
    let maxNonInkablesAllowed = deckSize - inkablesNeeded;

    setResult({
      inkablesNeeded,
      totalCardsSeen,
      maxNonInkablesAllowed,
    });
  }

  return (
    <div className="p-4 max-w-xl mx-auto space-y-6 bg-white shadow-lg rounded-2xl">
      <h1 className="text-2xl font-bold text-center">Lorcana Ink Curve Calculator</h1>

      <div>
        <label className="block font-semibold">Deck Size</label>
        <input
          type="number"
          value={deckSize}
          onChange={(e) => setDeckSize(Number(e.target.value))}
          className="w-full border p-2 rounded"
          min={40}
          max={80}
        />
      </div>

      <div>
        <label className="block font-semibold">Current Non-Inkables</label>
        <input
          type="number"
          value={nonInkables}
          onChange={(e) => setNonInkables(Number(e.target.value))}
          className="w-full border p-2 rounded"
          min={0}
          max={deckSize}
        />
      </div>

      <div>
        <label className="block font-semibold">Average Mana Curve</label>
        <input
          type="number"
          step="0.1"
          value={manaCurve}
          onChange={(e) => setManaCurve(Number(e.target.value))}
          className="w-full border p-2 rounded"
        />
      </div>

      <button
        onClick={calculateMinimumInkables}
        className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
      >
        Calculate
      </button>

      {result && (
        <div className="border-t pt-4 space-y-2">
          <p><strong>Turns Simulated:</strong> {Math.ceil(manaCurve)}</p>
          <p><strong>Cards Seen by Turn {Math.ceil(manaCurve)}:</strong> {result.totalCardsSeen}</p>
          <p><strong>Recommended Max Non-Inkables:</strong> {result.maxNonInkablesAllowed}</p>
          <p><strong>Current Non-Inkables:</strong> {nonInkables} {nonInkables > result.maxNonInkablesAllowed ? "⚠️ (Too Many!)" : "✅ (Looks Good)"}</p>
        </div>
      )}
    </div>
  );
}
